mapper.select:
select outTable.* 
from (
select rownum rum, inTable.* 
	  from (
	  	select * 
	  	from reportComment 
	  	order by writeDate desc
	  	) inTable  
	  order by rum
	  ) outTable 
where rum between #{startPage} and #{endPage}

select outTable.* 
	from (
	select rownum rum, inTable.* 
		  from (
		  	select * 
		  	from comments 
		  	where commentBad > 10 
		  	order by writeDate desc
		  	) inTable  
		  order by rum
		  ) outTable 
	where rum between #{startPage} and #{endPage}

select outTable.* 
	from (
	select rownum rum, inTable.* 
		  from (
		  	select * 
		  	from reportBook
		  	order by writeDate desc
		  	) inTable  
		  order by rum
		  ) outTable 
	where rum between #{startPage} and #{endPage}

select outTable.* 
	from (
	select rownum rum, inTable.* 
		  from (
		  	select * 
		  	from notice
		  	order by writeDate desc
		  	) inTable  
		  order by rum
		  ) outTable 
	where rum between #{startPage} and #{endPage}

select outTable.* 
	from (
	select rownum rum, inTable.* 
		  from (
		  	select * 
		  	from recommendedBook
		  	order by writeDate desc
		  	) inTable  
		  order by rum
		  ) outTable 
	where rum between #{startPage} and #{endPage}

mapper.update:
UPDATE NOTICE 
SET TITLE=#{title}, USERNUM=#{userNum}, TYPE=#{type}, CONTENTS=#{contents}, WRITEDATE = sysdate
WHERE ARTICLENUM=#{articleNum}

UPDATE RECOMMENDEDBOOK 
SET BOOKARTICLENUM=#{bookArticleNum}, USERNUM=#{userNum}, RECOMMENDCOMMENT=#{recommendComment}, WRITEDATE = sysdate
WHERE RECOMMENDNUM = #{recommendNum}
