mapper.select:
SELECT count(*) 
from bookInfo
where userNum=#{userNum}

SELECT count(*) 
from readBook
where userNum=#{userNum}

SELECT count(*) 
from bookWish
where userNum=#{userNum}

SELECT count(*) 
from bookMark
where userNum=#{userNum}

mapper.delete:
DELETE
FROM comments
where commentNum=#{commentNum}

	and C.depth=0


	and C.depth!=0

and userNum=#{userNum}

DELETE
FROM BOOKINFO
where bookArticleNum=#{bookArticleNum}
and userNum=#{userNum}

DELETE
FROM BOOKWISH
where bookArticleNum=#{wishNum}
and userNum=#{userNum}

DELETE
FROM READBOOK
where bookArticleNum=#{readBoolNum}
and userNum=#{userNum}

DELETE
FROM BOOKMARK
where bookArticleNum=#{bookMarkNum}
and userNum=#{userNum}
