mapper.update:
	UPDATE BOOKINFO 
    SET hit = hit + 1 
    WHERE FILELOCATION=#{fileLocation}

mapper.select:
select outTable.* 
	from (
	select rownum rum, inTable.* 
		  from (
		  	select * 
		  	from notice
		  	where type = 0 
		  	order by writeDate desc
		  	) inTable  
		  order by rum
		  ) outTable 
	where rum between #{startPage} and #{endPage}

select outTable.* 
	from (
	select rownum rum, inTable.* 
		  from (
		  	select * 
		  	from notice
		  	where type = 1 
		  	order by writeDate desc
		  	) inTable  
		  order by rum
		  ) outTable 
	where rum between #{startPage} and #{endPage}
